forever(function () {
    if (menu == 1) {
        pause(randint(800, 2000))
        mySprite = sprites.create(assets.image`suss`, SpriteKind.Enemy)
        if (light2 == 0) {
            mySprite.setPosition(10, 64)
            pause(1500)
            if (light2 == 0) {
                sprites.destroy(mySprite)
            } else {
                game.setGameOverEffect(false, effects.blizzard)
                game.gameOver(false)
            }
        }
        if (light1 == 1) {
            mySprite.setPosition(150, 64)
            pause(1500)
            if (light1 == 1) {
                sprites.destroy(mySprite)
            } else {
                game.setGameOverEffect(false, effects.blizzard)
                game.gameOver(false)
            }
        }
    }
})
